
public class WhileDemo0 {
	
	public static void main(String[] args) {
			// TODO Auto-generated method stub
			
		      int num =10;
			  while (num>0)  // eval condition)==true 
			{
				      // if condition is true, statement inside while loop is executed
				System.out.println("this is first line Java program :"+ num);
				num--;
			}
	            // if condition is false, then next line immediately following
			    // loop will be executed
		}
	}



